CREATE DATABASE IF NOT EXISTS `sei` DEFAULT CHARACTER SET utf8 COLLATE utf8_unicode_ci;USE `sei`;CREATE TABLE `usuario` (`codUsuario` int NOT NULL AUTO_INCREMENT,`login` varchar(100) NOT NULL,`senha` varchar(100) NOT NULL DEFAULT 123456,`nome` varchar(255) NOT NULL,`tipoUsuario` char(1) NULL,PRIMARY KEY (`codUsuario`) ,UNIQUE INDEX `un_login_usuario` (`login` ASC));CREATE TABLE `endereco` (`codUsuario` int NOT NULL,`cep` varchar(9) NULL,`rua` varchar(150) NULL,`nCasa` varchar(10) NULL,`bairro` varchar(50) NULL,`complemento` varchar(255) NULL,`cidade` varchar(50) NULL,`estado` varchar(50) NULL,PRIMARY KEY (`codUsuario`) );CREATE TABLE `aluno` (`matricAluno` int NOT NULL,`codTurmaAtual` int NULL,`idade` int NULL,`sexo` char(1) NULL,PRIMARY KEY (`matricAluno`) );CREATE TABLE `turma` (`codTurma` int NOT NULL AUTO_INCREMENT,`serie` int(1) NULL,`turno` char(1) NULL,`ano` int(4) NULL,`sala` varchar(50) NULL,PRIMARY KEY (`codTurma`) );CREATE TABLE `disciplina` (`codDisciplina` int NOT NULL AUTO_INCREMENT,`nome` varchar(50) NOT NULL,`sigla` varchar(5) NULL,`codProfessor` int NOT NULL,`codTurma` int NOT NULL,PRIMARY KEY (`codDisciplina`) );CREATE TABLE `dadosPessoais` (`codUsuario` int NOT NULL,`cpf` varchar(14) NULL,`rg` varchar(9) NULL,`email` varchar(100) NULL,`telefone` varchar(15) NULL,`celular` varchar(15) NULL,PRIMARY KEY (`codUsuario`) );CREATE TABLE `professor` (`codProfessor` int NOT NULL,`dataAdmissao` date NULL,PRIMARY KEY (`codProfessor`) );CREATE TABLE `nota` (`matricAluno` int NOT NULL,`codDisciplina` int NOT NULL,`ano` int(4) NOT NULL,`bimestre` int(1) NOT NULL,`nota` decimal(4,2) NULL DEFAULT 0,PRIMARY KEY (`matricAluno`, `codDisciplina`, `ano`, `bimestre`) );ALTER TABLE `disciplina` ADD CONSTRAINT `fk_disciplina_professor_1` FOREIGN KEY (`codProfessor`) REFERENCES `professor` (`codProfessor`);ALTER TABLE `disciplina` ADD CONSTRAINT `fk_disciplina_turma_1` FOREIGN KEY (`codTurma`) REFERENCES `turma` (`codTurma`);ALTER TABLE `aluno` ADD CONSTRAINT `fk_aluno_turma_1` FOREIGN KEY (`codTurmaAtual`) REFERENCES `turma` (`codTurma`);ALTER TABLE `nota` ADD CONSTRAINT `fk_nota_aluno_1` FOREIGN KEY (`matricAluno`) REFERENCES `aluno` (`matricAluno`);ALTER TABLE `nota` ADD CONSTRAINT `fk_nota_disciplina_1` FOREIGN KEY (`codDisciplina`) REFERENCES `disciplina` (`codDisciplina`);ALTER TABLE `endereco` ADD CONSTRAINT `fk_endereco_dados_pessoais_1` FOREIGN KEY (`codUsuario`) REFERENCES `dadosPessoais` (`codUsuario`);ALTER TABLE `aluno` ADD CONSTRAINT `fk_aluno_usuario_1` FOREIGN KEY (`matricAluno`) REFERENCES `usuario` (`codUsuario`);ALTER TABLE `professor` ADD CONSTRAINT `fk_professor_usuario_1` FOREIGN KEY (`codProfessor`) REFERENCES `usuario` (`codUsuario`);ALTER TABLE `dadosPessoais` ADD CONSTRAINT `fk_dadosPessoais_usuario_1` FOREIGN KEY (`codUsuario`) REFERENCES `usuario` (`codUsuario`);INSERT INTO `usuario` (`login`, `senha`, `nome`, `tipoUsuario`) VALUES ('admin', '1234qwer', 'Administrador Master', '0');